import React from 'react'

const Signup = () => {
  const [credentials, setCredentials] = useState({email: "" , password: ""});
//let history = useHistory();
const navigate = useNavigate();
  const handleSubmit = async (e) => {
      e.preventDefault();
      const response = await fetch(`http://localhost:5000/api/auth/login`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          // "auth-token"  : "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjY1MTc1ODJkOTdkYjYyNGI1OTdkYTQzIn0sImlhdCI6MTcxNjY5ODY5NH0.2-EggWghfZ_X4-dP_G-g9gk3VmN-nBw7JcR0Ck4Xkws"
        },      body: JSON.stringify({email: credentials.email, password : credentials.password})
      });
    const json = await response.json();
    console.log(json);
    if(json.success){
        //save the auth toke and redirect
        localStorage.setItem('token', json.authtoken);
        // history.push("/");
        navigate('/');

    }else{
        alert("invalid creditials");
    }
  }

  const onChange = (e)=>{
    setCredentials({...credentials, [e.target.name]: e.target.value})
}

  return (
    <div>
      <form>
      <div class="mb-3">
    <label for="name" class="form-label">Name</label>
    <input type="text" class="form-control" id="name" onChange={onChange} name="name" aria-describedby="emailHelp"/>
    </div> 
  <div class="mb-3">
    <label for="email" class="form-label">Email address</label>
    <input type="email" class="form-control" id="email" onChange={onChange} name="email" aria-describedby="emailHelp"/>
    <div id="emailHelp" class="form-text">We'll never share your email with anyone else.</div>
  </div>
  <div class="mb-3">
    <label for="exampleInputPassword1" class="form-label">Password</label>
    <input type="password" class="form-control" onChange={onChange} id="exampleInputPassword1"/>
  </div>
  <div class="mb-3">
    <label for="password" class="form-label"> Confirm Password</label>
    <input type="password" class="form-control" onChange={onChange} id="password" name="password"/>
  </div>
  
  <button type="submit" class="btn btn-primary">Submit</button>
</form>
    </div>
  )
}

export default Signup
