import React, { useContext, useEffect, useRef, useState } from 'react';
import noteContext from '../context/notes/nodeContext';
import Noteitem from './Noteitem';
import AddNote from './AddNote';

const Note = () => {
    const context = useContext(noteContext);
    const { notes, getNotes } = context;
    
    
    const ref = useRef(null);
    let [note, setNote] = useState({etitle: "", edescription: "", etag : ""})
    
    const updateNote = (id) => {
        ref.current.click();
        const currentNote = <notes className="findById"></notes>(note._id === id);

       if(currentNote)  
                {setNote({ etitle: currentNote.title, edescription: currentNote.description, etag: currentNote.tag });}

    }

    useEffect(() => {
        getNotes();
       // eslint-disable-next-line

    }, [])

    // useEffect(() => {
    //     // Update input field values when notes change
    //     if (notes.length > 0) {
    //          setNote({
    //             etitle: notes._id.title,
    //             edescription: notes._id.description,
    //             etag: notes._id.tag,
                
                    
            
    //         });
    //     }
    // } , [notes]);

    const handleClick = (e)=>{
        e.preventDefault();
    }
    
//   const onChange = (e)=>{
//       setNote({...note, [e.target.name]: e.target.value})
//   }


    return (
        <>
            <AddNote />
            <button type="button" ref={ref} className="btn btn-primary d-none" data-bs-toggle="modal" data-bs-target="#exampleModal">
                Launch demo modal
            </button>
            <div className="modal fade" id="exampleModal"  tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title" id="exampleModalLabel">Edit Note</h5>
                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div className="modal-body">
                            <form>
                                <div className="mb-3">
                                    <label htmlFor="etitle" className="form-label">Title</label>
                                    <input type="text"  className="form-control" id="etitle" name="etitle" value={note.etitle} aria-describedby="emailHelp" onChange={(e) => setNote({ ...note, etitle: e.target.value })} />
                                </div>
                                <div className="mb-3">
                                    <label htmlFor="edescription" className="form-label">Description</label>
                                    <input type="text"  className="form-control" id="edescription" name="edescription" value={note.edescription} onChange={(e) => setNote({ ...note, etitle: e.target.value })} />
                                </div>
                                <div className="mb-3">
                                    <label htmlFor="etag"  className="form-label">Tag</label>
                                    <input type="text" className="form-control" id="etag" name="etag" value={note.etag} onChange={(e) => setNote({ ...note, etitle: e.target.value })} />
                                </div>
                                
                            </form>
                        </div>
                        <div className="modal-footer">
                            <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                            <button type="button" className="btn btn-primary">Update Note</button>
                        </div>
                    </div>
                </div>
            </div>
            <div className=" row cantainer my-3">
                <h1>Your Note</h1>
                {notes.map((note) => {
                    return <Noteitem key={note._id} note={note} updateNote={updateNote} />

                })}

            </div>
        </>
    )
}

export default Note



