import React, { useContext, useState } from 'react';
import noteContext from '../context/notes/nodeContext';


const AddNote = () => {
    const context = useContext(noteContext);
    const {addNote } = context;
    const [note, setNote] = useState({title: "", description: "", tag : "default"})

    const handleClick = (e)=>{
      e.preventDefault();
      addNote(note.title, note.description, note.tag);
  }

  const onChange = (e)=>{
      setNote({...note, [e.target.name]: e.target.value})
  }
  return (

      <div className="cantainer my-3">
      <h1>Add Note</h1>
      <form>
        <div className="mb-3">
          <label htmlFor="title" className="form-label">Title</label>
          <input type="text" className="form-control" id="title" name="title" aria-describedby="emailHelp" onChange={onChange} minLength={5} required/>
        </div>
        <div className="mb-3">
          <label htmlFor="description" className="form-label">Description</label>
          <input type="text" className="form-control" id="description" name="description" onChange={onChange} minLength={5} required/>
        </div>
        <div className="mb-3">
          <label htmlFor="tag" className="form-label">Tag</label>
          <input type="text" className="form-control" id="tag" name="tag" onChange={onChange} minLength={3} required/>
        </div>
        <button disabled={note.title.length<5 || note.description.length<5 ||note.tag.minLength=== 5 } type="submit" className="btn btn-primary" onClick={handleClick}>Add Note</button>
      </form>

    </div>
  )
}

export default AddNote
